package com.smallworldfs.moneytransferapp.data.transactional.cashpickup.repository.network

import com.smallworldfs.moneytransferapp.data.base.network.NetworkDatasource
import com.smallworldfs.moneytransferapp.data.transactional.cashpickup.model.CashPickUpLocationsResponse
import com.smallworldfs.moneytransferapp.data.transactional.cashpickup.model.RequestCashPickUpChooseLocationDataModel
import com.smallworldfs.moneytransferapp.data.transactional.cashpickup.model.RequestCashPickUpLocationsDataModel
import com.smallworldfs.moneytransferapp.data.transactional.cashpickup.model.ResponseCashPickUpChooseLocationDataModel
import javax.inject.Inject

class CashPickUpNetwork @Inject constructor(private val service: CashPickUpService) : NetworkDatasource() {

    suspend fun requestCashPickupLocationsAsync(cashPickupLocationsRequestCashPickUpLocationsDataModel: RequestCashPickUpLocationsDataModel): CashPickUpLocationsResponse? =
        executeCall(service.requestCashPickupLocationsAsync(cashPickupLocationsRequestCashPickUpLocationsDataModel.userToken, cashPickupLocationsRequestCashPickUpLocationsDataModel.userId, cashPickupLocationsRequestCashPickUpLocationsDataModel.amount, cashPickupLocationsRequestCashPickUpLocationsDataModel.currencyType, cashPickupLocationsRequestCashPickUpLocationsDataModel.currencyOrigin, cashPickupLocationsRequestCashPickUpLocationsDataModel.beneficiaryId))

    suspend fun requestCashPickupChooseLocationAsync(requestCashPickUpChooseLocationDataModel: RequestCashPickUpChooseLocationDataModel): ResponseCashPickUpChooseLocationDataModel? =
        executeCall(service.requestCashPickupChooseLocationAsync(requestCashPickUpChooseLocationDataModel.userId, requestCashPickUpChooseLocationDataModel.userToken, requestCashPickUpChooseLocationDataModel.deliveryMethod, requestCashPickUpChooseLocationDataModel.locationCode, requestCashPickUpChooseLocationDataModel.representativeCode, requestCashPickUpChooseLocationDataModel.beneficiaryId, requestCashPickUpChooseLocationDataModel.pickUpFee, requestCashPickUpChooseLocationDataModel.pickUpRate))
}
